/* ---------- Enhanced Process_Text with shorter, friendlier commands ---------- */
const textIn = msg.originalMessage?.text || msg.payload?.content || '';
const txt = textIn.trim();
const chatId = msg.payload.chatId;

/* ---------- /set <param> <value> ---------- */
const setCmd = txt.match(/^\/set\s+(\w+)\s+([\d.]+)$/i);
if (setCmd) {
    msg.route = 'threshold';
    msg.param = setCmd[1].toLowerCase();
    msg.value = Number(setCmd[2]);
    msg.chat = chatId;
    return msg;
}

/* ---------- /limits or /settings ---------- */
if (/^\/(?:limits|settings)$/i.test(txt)) {
    msg.route = 'thresholdStatus';
    msg.chat = chatId;
    return msg;
}

/* ---------- /help or /start ---------- */
if (/^\/(?:help|start|commands)$/i.test(txt)) {
    msg.route = 'help';
    msg.chat = chatId;
    return msg;
}

/* ---------- /config (show available settings) ---------- */
if (/^\/config$/i.test(txt)) {
    msg.route = 'thresholdCheat';
    msg.chat = chatId;
    return msg;
}

/* ---------- /find <query> ---------- */
const fnd = txt.match(/^\/find\s+(.+)/i);
if (fnd) {
    msg.route = 'find';
    msg.query = fnd[1].trim();
    msg.chat = chatId;
    return msg;
}



/* ---------- /traffic ---------- */
if (/^\/traffic$/i.test(txt)) {
    msg.route = 'traffic';
    msg.chat = chatId;
    return msg;
}
/* ---------- /status ---------- */
if (/^\/status$/i.test(txt)) {
    msg.route = 'status';
    msg.chat = chatId;
    return msg;
}
/* ---------- /location <city> [CC] ---------- */
const loc = txt.match(/^\/(?:location|loc)\s+(.+)/i);
if (loc) {
    let raw = loc[1]
        .replace(/\s*,\s*/g, ',')
        .replace(/\s+/g, ' ')
        .trim();

    let cityPart = raw;
    let ccPart = '';
    const ccRE = /^[A-Z]{2}$/;

    if (raw.includes(',')) {
        const parts = raw.split(',');
        cityPart = parts[0].trim();
        ccPart = parts[1].trim().toUpperCase();
    } else {
        const parts = raw.split(' ');
        if (ccRE.test(parts.at(-1).toUpperCase())) {
            ccPart = parts.pop().toUpperCase();
            cityPart = parts.join(' ');
        }
    }

    msg.route = 'location';
    msg.cityName = cityPart;
    msg.country = ccPart;
    msg.chat = chatId;
    return msg;
}

