function validateSetting(key, value) {
    const validations = {
        temp_high: { min: -50, max: 60, unit: '¬∞C' },
        temp_low: { min: -50, max: 50, unit: '¬∞C' },
        quake_mag: { min: 1.0, max: 10.0, unit: '' },
        quake_radius: { min: 1, max: 1000, unit: 'km' },
        global_quake: { min: 4.0, max: 10.0, unit: '' },
        traffic_severity: { min: 1, max: 5, unit: '' },
        search_radius: { min: 0.1, max: 100, unit: 'km' },
        search_limit: { min: 1, max: 20, unit: '' },
        aqi_limit: { min: 1, max: 500, unit: '' },
        pollen_limit: { min: 1, max: 10, unit: '' }
    };

    // Map alternative names
    const keyMap = {
        'near_quake_mag': 'quake_mag',
        'near_quake_radius': 'quake_radius',
        'global_quake_mag': 'global_quake',
        'traffic_min_sev': 'traffic_severity',
        'radius_near_me': 'search_radius',
        'nearby_limit': 'search_limit'
    };

    const normalizedKey = keyMap[key] || key;
    const validation = validations[normalizedKey];

    if (!validation) {
        return { 
            valid: true, 
            message: '' 
        }; // Allow unknown keys for flexibility
    }

    if (typeof value !== 'number' || value < validation.min || value > validation.max) {
        return {
            valid: false,
            message: `${key} must be between ${validation.min} and ${validation.max}${validation.unit}`
        };
    }

    return { valid: true, message: '' };
}

