        threshold: tempHigh,
        icon: 'üö®',
        message: `**CRITICAL**: ${currentTemp.toFixed(1)}¬∞C ‚â• ${tempHigh}¬∞C threshold!`
    });
} else if (currentTemp >= (tempHigh - 1)) {
    // Approaching threshold - WARNING
    alerts.push({
        type: 'warning_high',
        temp: currentTemp,
        threshold: tempHigh,
        icon: 'üî•',
        message: `**WARNING**: ${currentTemp.toFixed(1)}¬∞C approaching ${tempHigh}¬∞C threshold!`
    });
}

// LOW TEMPERATURE ALERTS
if (currentTemp <= tempLow) {
    // Already at threshold - URGENT
    alerts.push({
        type: 'critical_low',
        temp: currentTemp,
        threshold: tempLow,
        icon: 'üßä',
        message: `**CRITICAL**: ${currentTemp.toFixed(1)}¬∞C ‚â§ ${tempLow}¬∞C threshold!`
