// ANTI-SPAM SYSTEM - Only send if state changed
// ‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê

if (alerts.length > 0) {
    const alertKey = `temp_state:${chatId}`;
    const currentState = alerts.map(a => a.type).sort().join(',');
    const lastState = global.get(alertKey);

    // Only send if state changed (prevents spam)
    if (lastState !== currentState) {
        global.set(alertKey, currentState);

        alerts.forEach(alert => {
            const alertText = `${alert.icon} **Temperature Alert**\n\n` +
