  } else {
    timestampMs = Date.now();
  }

  let lastUpdateMs;
  if (prop.lastupdate) {
    lastUpdateMs = new Date(prop.lastupdate).getTime();
  } else {
    lastUpdateMs = timestampMs;
  }

  const quake = {
    quake_id: prop.id || q.id,
    ts_utc: timestampMs,
    last_upd: lastUpdateMs,
    lat: geom.coordinates[1],
