  const pr = feat.properties || {};

  /* ---- derive severity --------------------------------------------- */
  let sev = Number(pr.severity);
  if (!Number.isFinite(sev) || sev < 1 || sev > 5) {
    if (Number.isFinite(pr.magnitudeOfDelay) && pr.magnitudeOfDelay > 0) {
      sev = clamp(Math.ceil(pr.magnitudeOfDelay / 600), 1, 5);
    } else {
      sev = cat2sev[pr.iconCategory] ?? 2;
    }
  }

  /* ---- coordinates ------------------------------------------------- */
  let lat = null, lon = null;
  if (feat.geometry?.type === 'LineString') {
    [lon, lat] = feat.geometry.coordinates[0];
  } else if (feat.geometry?.type === 'Point') {
    [lon, lat] = feat.geometry.coordinates;
  }
  if (lat == null || lon == null) continue;

  /* ---- distance calculation ---------------------------------------- */
  const distKm = haversine(userLat, userLon, lat, lon);

  /* ---- other fields ------------------------------------------------ */
  const tsIso = iso(pr.startTime || pr.from || pr.t);
  const delay = pr.magnitudeOfDelay ?? 0;
  const cat = pr.iconCategory ?? 0;

  /* ---- SQL UPSERT -------------------------------------------------- */
  const sql = `
    INSERT INTO traffic_incidents
      (ts_utc, lat, lon, severity, delay_sec, category, src)
    VALUES
      ('${tsIso}', ${lat}, ${lon}, ${sev}, ${delay}, ${cat}, 'tomtom')
    ON CONFLICT(ts_utc,lat,lon,src) DO UPDATE SET
      severity  = excluded.severity,
      delay_sec = excluded.delay_sec;
  `.trim();
  node.send([{ topic: sql }, null]);

  /* ---- collect for alert ------------------------------------------- */
  if (sev >= minSev) {
    bucket[sev] = (bucket[sev] ?? 0) + 1;

