WITH current_weather AS (
  SELECT temperature, humidity, wind_speed, uv_index, pressure,
         ROUND((julianday('now') - julianday(timestamp)) * 24, 1) as hours_ago
  FROM weather_current_ow 
  WHERE ROUND(latitude,3) = ${latStr} AND ROUND(longitude,3) = ${lonStr}
