  ORDER BY ts_utc DESC
),
recent_traffic AS (
  SELECT COUNT(*) as incidents,
         MAX(severity) as max_severity,
         AVG(delay_sec) as avg_delay
  FROM traffic_incidents 
  WHERE ts_utc > datetime('now', '-2 hours')
    AND severity >= ${trafficSev}
    AND ROUND(lat,3) BETWEEN ${(lat - 0.01).toFixed(3)} AND ${(lat + 0.01).toFixed(3)}
    AND ROUND(lon,3) BETWEEN ${(lon - 0.01).toFixed(3)} AND ${(lon + 0.01).toFixed(3)}
),
recent_quakes AS (
  SELECT COUNT(*) as count,
         MAX(magnitude) as max_magnitude,
         MIN(ROUND((111.19 * ACOS(
           COS(RADIANS(${lat})) * COS(RADIANS(lat)) * 
           COS(RADIANS(lon - ${lon})) + 
           SIN(RADIANS(${lat})) * SIN(RADIANS(lat))
         )), 1)) as min_distance_km,
         ROUND((julianday('now') - julianday(MAX(ts_utc))) * 24, 1) as hours_since_last
