/***********************************************************************
 * km_process ‚Äì 1-D K-means on historical temps + today‚Äôs real mean
 * INPUT : msg.payload  ‚Üí Array[{ yr, temp_mean }]
 *         msg.todayMean‚Üí Number  (calculated in parse_today_mean)
 * OUTPUT0: { topic: <UPSERT SQL> }  ‚Üí SQLite
 * OUTPUT1: { chatId, payload:{‚Ä¶} }  ‚Üí Telegram (optional alert)
 ***********************************************************************/

const rows      = msg.payload || [];
const todayMean = msg.todayMean;             // comes from parse_today_mean

if (!rows.length)  { node.warn('km_process: no history rows'); return null; }
if (todayMean == null) { node.warn('km_process: no todayMean'); return null; }

/* -------- build clustering vector (exclude current year) -------------- */
const thisYear = new Date().getUTCFullYear();

const temps = rows
  .filter(r => r.yr !== thisYear && r.temp_mean != null)   // history only
  .map(r   => r.temp_mean);

if (temps.length < 4) {
  node.warn(`km_process: need ‚â•4 temps, got ${temps.length}`);
  return null;
}

/* -------- simple 1-D K-means (k = 4) ---------------------------------- */
const k = 4;
let centroids = temps.slice(0, k);           // seed with first k values

for (let iter = 0; iter < 30; iter++) {
  const clusters = Array.from({ length: k }, () => []);
  temps.forEach(t => {
    const idx = centroids.reduce(
      (best, c, i, arr) =>
        Math.abs(t - c) < Math.abs(t - arr[best]) ? i : best,
      0
    );
    clusters[idx].push(t);
  });
  centroids = clusters.map((c, i) =>
    c.length ? c.reduce((a, b) => a + b, 0) / c.length : centroids[i]
  );
}

/* -------- pick cluster closest to todayMean --------------------------- */
const cIdx = centroids.reduce(
  (best, c, i, arr) =>
    Math.abs(todayMean - c) < Math.abs(todayMean - arr[best]) ? i : best,
  0
);

const clusterVals = temps.filter(t => {
  const idx = centroids.reduce(
    (best, c, i, arr) =>
      Math.abs(t - c) < Math.abs(t - arr[best]) ? i : best,
    0
  );
  return idx === cIdx;
});

const mu = centroids[cIdx];
const sigma =
  Math.sqrt(
    clusterVals.reduce((a, t) => a + (t - mu) ** 2, 0) / clusterVals.length
  ) || 0.0001; // guard against zero
global.set('climate_mu', mu);
global.set('climate_sigma', sigma);
global.set('climate_todayMean', todayMean); 
/* -------- UPSERT into climate_centroids ------------------------------- */
const lat  = Number(global.get('user_lat').toFixed(3));
const lon  = Number(global.get('user_lon').toFixed(3));
const mmdd = new Date().toISOString().slice(5, 10); // "06-15"

const upsert = `
INSERT INTO climate_centroids
  (mm_dd, lat_round, lon_round, k, mu, sigma, updated_at)
VALUES
  ('${mmdd}', ${lat}, ${lon}, ${k}, ${mu}, ${sigma},
   datetime('now','utc'))
ON CONFLICT(mm_dd, lat_round, lon_round) DO UPDATE SET
  k          = excluded.k,
  mu         = excluded.mu,
  sigma      = excluded.sigma,
  updated_at = excluded.updated_at;
`;

node.send([{ topic: upsert }, null]);        // output 0 ‚Üí SQLite

/* -------- optional anomaly alert -------------------------------------- */
const z = (todayMean - mu) / sigma;
if (Math.abs(z) >= 2) {
  const txt =
    `üå°Ô∏è *Temp anomaly*` +
    `\nToday mean: ${todayMean.toFixed(1)} ¬∞C` +
    `\nClimatology: ${mu.toFixed(1)} ¬± ${sigma.toFixed(1)} ¬∞C (k=${k})` +
    `\n*z-score: ${z.toFixed(1)}*`;
  node.send([
    null,
    {
      chatId: global.get('default_chat'),
      payload: {
        chatId: global.get('default_chat'),
        type: 'message',
        content: txt
      }
    }
  ]);
}

return null;

